function makeResult(parsedResult){
	var name = parsedResult['primer_nombre'];
    	var middleName = parsedResult['segundo_nombre'];
    	var lastName = parsedResult['primer_apellido'];
    	var secondLastName = parsedResult['segundo_apellido'];

    	var doctorName = name;
    	if(middleName){
    		doctorName = doctorName + " " + middleName;
    	};

    	if(lastName) {
    		doctorName = doctorName + " " + lastName;
    	}

    	if(secondLastName) {    		doctorName = doctorName + " " + secondLastName;    	}

	channelMap.put('info', JSON.stringify({
			"medic_register": parsedResult['registro_med'],
			"dni": parsedResult['identificacion'],
			"dni_type": parsedResult['tipo_identificacion'],
			"name": doctorName,
			"email": parsedResult['e_mail'],
			"phone_primary": parsedResult['telefono'],
			"phone": parsedResult['celular'],
			"birthday": parsedResult['fecha_de_nacimiento'],
			"payment_method": parsedResult['forma_pago'],
			"bank_account_number": parsedResult['cuenta_bancaria'],
			"bank_account_type": parsedResult['tipo_cuenta'],
			"observations": parsedResult['observaciones'],
		}));
};

//Imprimir el mensaje para verificar que todo sea correcto
// logger.info("Mensaje XMLm extraido" + xmlMessage);

