var destinationPatterns = [
	"test-especialidad",
	"nueva-especialidad",
	"editar-especialidad",
	"eliminar-especialidad",
	"test-doctor",
	"nuevo-doctor",
	"editar-doctor",
	"eliminar-doctor"
];

