	rawMsg = rawMsg.substring(hasHash + 1);
};

var parsedMessage = JSON.parse(rawMsg);
var xmlMessage = parsedMessage.data;
var pattern = parsedMessage.pattern;

if(!destinationPatterns.includes(pattern)){
	destinationSet.removeAll();	
};

