properties.select:
// Obtener la conexión de la base de datos desde el global map.
var dbConn = globalChannelMap.get("dbConn");
var batchSize = 100;  // Tamaño de cada lote.
var lastRow = globalChannelMap.get("lastRow-pacientes-seeder") || 0;  // Último valor de fila almacenado para paginación.
var startRow = lastRow + 1;  // Fila inicial del próximo lote.
var endRow = startRow + batchSize - 1;  // Fila final del próximo lote.

var sql = "SELECT * FROM (SELECT VW_ADM_PACIENTE.*, ROWNUM AS RN FROM VW_ADM_PACIENTE WHERE ROWNUM <= " + endRow + ") WHERE RN >= " + startRow;

if (dbConn == null || dbConn.getConnection().isClosed()) {
    // Crear la conexión.
    dbConn = DatabaseConnectionFactory.createDatabaseConnection(
        globalChannelMap.get("dbDriver"),
        globalChannelMap.get("dbUrl"),
        globalChannelMap.get("dbUser"),
        globalChannelMap.get("dbPassword")
    );
    globalChannelMap.put("dbConn", dbConn);
}

try {
    // Ejecutar la consulta con paginación.
    var result = dbConn.executeCachedQuery(sql);
    logger.info("Records fetched: " + result.size());

    // Actualizar la posición de `lastRow-pacientes-seeder` en el global map si hay resultados.
    if (result.size() > 0) {
        globalChannelMap.put("lastRow-pacientes-seeder", endRow);
    } else {
        logger.info("No more data to fetch. Resetting lastRow.");
        globalChannelMap.put("lastRow-pacientes-seeder", 0);  // Reiniciar si no hay más datos.
    }
} catch (e) {
    // Registrar el error.
    logger.error("Exception in Database Reader: " + e);

    // Quitar la variable de conexión para que se recree en la siguiente iteración.
    globalChannelMap.remove("dbConn");
}

dbConn.close();
return result;

