// Get the database connection from the global map (if there is one at this point).
var dbConn = globalChannelMap.get("dbConn");
var dbUrl="jdbc\:oracle\:thin\:@129.213.150.201\:1521\:SHDB"; //PRODUCCION
var dbUser="SGHVBDS";
var dbPassword="SGHVBDS21#";
var dbDriver="oracle.jdbc.driver.OracleDriver";

globalChannelMap.put("dbUrl", dbUrl);
globalChannelMap.put("dbUser", dbUser);
globalChannelMap.put("dbPassword", dbPassword);
globalChannelMap.put("dbDriver", dbDriver);
// If there is not currently a database connection...
if (dbConn == null || dbConn.getConnection().isClosed()) {
	// Create the connection.
logger.info("creating connection: "+dbUser);

	dbConn = DatabaseConnectionFactory.createDatabaseConnection(globalChannelMap.get("dbDriver"),globalChannelMap.get("dbUrl"),globalChannelMap.get("dbUser"),globalChannelMap.get("dbPassword"));

logger.info("set globla connection: ");
	//script Put the new connection into the global map.
	globalChannelMap.put("dbConn", dbConn);
}

return;

